<?php /** * socialnetworking.solutions * * @category   Application_Modules * @package    Epetition * @copyright  Copyright 2014-2019 Ahead WebSoft Technologies Pvt. Ltd. * @license    https://socialnetworking.solutions/license/ * @version    $Id: Contactinformation.php 2019-11-07 00:00:00 socialnetworking.solutions $ * @author     socialnetworking.solutions */class Epetition_Form_Dashboard_Contactinformation extends Engine_Form{  public function init()  {    $this->setTitle('Petition Contact Information')      ->setAttrib('id', 'epetition_ajax_form_submit')      ->setMethod("POST")      ->setAction(Zend_Controller_Front::getInstance()->getRouter()->assemble(array()));    // Name    $this->addElement('Text', 'petition_contact_name', array(      'label' => 'Name',      'allowEmpty' => false,      'required' => true,      'validators' => array(        array('NotEmpty', true),        array('StringLength', false, array(1, 64)),      ),      'filters' => array(        'StripTags',        new Engine_Filter_Censor(),      ),    ));    // Email    $this->addElement('Text', 'petition_contact_email', array(      'label' => 'Email',      'allowEmpty' => false,      'required' => true,      'validators' => array(        'EmailAddress',      )    ));    // Phone    $this->addElement('Text', 'petition_contact_phone', array(      'label' => 'Phone',      'onkeypress' => 'return allowOnlyNumbers(event);',    ));    // Facebook    $this->addElement('Text', 'petition_contact_facebook', array(      'label' => 'Facebook',    ));    $this->addElement('Text', 'petition_contact_twitter', array(      'label' => 'Twitter',    ));    $this->addElement('Text', 'petition_contact_linkedin', array(      'label' => 'Linkedin',    ));    // Website    $this->addElement('Text', 'petition_contact_website', array(      'label' => 'Website',    ));    $this->addElement('Button', 'submit', array(      'label' => 'Save Contact Information',      'type' => 'submit',      'ignore' => true,      'decorators' => array(        'ViewHelper',      ),    ));//    $this->addDisplayGroup(array('submit'), 'buttons', array(//      'decorators' => array(//	'FormElements',//	'DivDivDivWrapper',//      ),//    ));  }}