<?php/** * SocialEngineSolutions * * @category   Application_Sesarticle * @package    Sesarticle * @copyright  Copyright 2015-2016 SocialEngineSolutions * @license    http://www.socialenginesolutions.com/license/ * @version    $Id: Style.php 2016-07-23 00:00:00 SocialEngineSolutions $ * @author     SocialEngineSolutions */class Sesarticle_Form_Dashboard_Style extends Engine_Form {  public function init() {    $this    ->setTitle('Article Styles')    ->setAttrib('id', 'sesarticle_ajax_form_submit')    ->setMethod("POST")    ->setAction(Zend_Controller_Front::getInstance()->getRouter()->assemble(array()));    $this->removeDecorator('FormWrapper');    // Element: style    $this->addElement('Textarea', 'style', array(      'label' => 'Custom Sesarticle Styles',      'description' => 'You can change the colors, fonts, and styles of your article by adding CSS code below. The contents of the text area below will be output between <style> tags on your event.'    ));        // Element: submit    $this->addElement('Button', 'submit', array(      'label' => 'Save Changes',      'type' => 'submit',    ));        // DisplayGroup: buttons    //$this->addDisplayGroup(array('submit'), 'buttons', array());    $this->addElement('Hidden', 'id');  }}