<?php/** * SocialEngineSolutions * * @category   Application_Sesarticle * @package    Sesarticle * @copyright  Copyright 2015-2016 SocialEngineSolutions * @license    http://www.socialenginesolutions.com/license/ * @version    $Id: Controller.php 2016-07-23 00:00:00 SocialEngineSolutions $ * @author     SocialEngineSolutions */class Sesarticle_Widget_FeaturedSponsoredVerifiedRandomArticleController extends Engine_Content_Widget_Abstract {  public function indexAction() {      $this->view->socialshare_enable_plusicon = $this->_getParam('socialshare_enable_plusicon', 1);    $this->view->socialshare_icon_limit = $this->_getParam('socialshare_icon_limit', 2);        $value['category_id'] = $this->_getParam('category','');    $value['criteria'] = $this->_getParam('criteria', 5);    $value['info'] = 'random';    $value['order'] = $this->_getParam('order', '');    $value['fetchAll'] = true;    $value['limit'] = 3;		//echo '<pre>';print_r($this->_getParam('show_criteria','sadad'));die;    $show_criterias = $this->_getParam('show_criteria', array('title','category'));    foreach ($show_criterias as $show_criteria)    $this->view->{$show_criteria . 'Active'} = $show_criteria;    $this->view->articles = Engine_Api::_()->getDbTable('sesarticles', 'sesarticle')->getSesarticlesSelect($value);    if(count($this->view->articles) < 1)    $this->setNoRender();  }}